import React , {Fragment , useEffect , useState} from "react";
import "../App.css";





const ListJobs = () => {
  
  const [job , setJobs] = useState([]);

  const getJobs = async () => {
    try{
      
      const response = await fetch("http://localhost:3001/jobs");
      const jsonData = await response.json();

      setJobs(jsonData);
      console.log(jsonData)

    }catch(err){
      console.error(err.message);
      
    }
  };

  useEffect(() => {
    getJobs();
  }, []);
  return (
    <>
      <div
      >
        <h1
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '40vh'
          }}>Find The Job You Love !</h1>
          {job.job_title}
      </div>
      <p>Featured Jobs</p>

      <div className="Catalog">
      <header className="cat-header">
        
      </header>
      <div className="user-container">
        
        <h5 className="info-item">{this.state.job_title}</h5>
        <h5 className="info-item">{job.comp_name}</h5>
        <h5 className="info-item">{job.job_location}</h5>
        <h5 className="info-item">{job.skills}</h5>
      </div>
    </div>
      
      
    </>
  );
};




export default ListJobs;


