import React , {Fragment , useEffect , useState} from "react";
import "../App.css";





const ListJobs = () => {
  
  const [job , setJobs] = useState([]);

  const getJobs = async () => {
    try{
      
      const response = await fetch("http://localhost:3001/jobs");
      const jsonData = await response.json();

      setJobs(jsonData);

    }catch(err){
      console.error(err.message);
      
    }
  };

  useEffect(() => {
    getJobs();
  }, []);
  if(getJobs.jsonData){
    content = jobs.jsonData.map((job , key ) => 
    <div>
      {job.job_title}
    </div>
    
    )
  }

  let content = null;




  return (
    <>
      <div
      >
        <h1
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '40vh'
          }}>Find The Job You Love !</h1>
      </div>
      <p>Featured Jobs</p>
      {content}
      
    </>
  );
};




export default ListJobs;


