          <thead>
            <tr>
              <th>Job Title</th>
              <th>Company Name</th>
              <th>Location</th>
              <th>Skills Required</th>
            </tr>
          </thead>
          <tbody>
            {job.map(jobs => (
              <tr key={jobs.job_id}>
                <td>{jobs.job_title}</td>
                <td>{jobs.comp_name}</td>
                <td>{jobs.job_location}</td>
                <td>{jobs.skills}</td>

              </tr>

            ))}
          </tbody>
        </table>
